// Process Claude API response
const response = $input.first().json;

// Check for errors
if (response.error) {
  return [{ json: { type: 'error', error: response.error.message || JSON.stringify(response.error) } }];
}

// Check if response has content
if (!response || !response.content) {
  return [{ json: { type: 'error', error: 'Invalid API response: ' + JSON.stringify(response) } }];
}

const results = [];

// Process content blocks
for (const block of response.content) {
  if (block.type === 'text') {
    results.push({ json: { type: 'text', content: block.text } });
  } else if (block.type === 'tool_use') {
    results.push({ 
      json: { 
        type: 'tool_call', 
        id: block.id,
        name: block.name, 
        input: block.input 
      } 
    });
  }
}

// Add done event
results.push({ json: { type: 'done', stop_reason: response.stop_reason } });

return results;
